// Simple API client to centralize base path, JSON handling, and auth header
export async function apiRequest(path, { method = 'GET', headers = {}, body, auth = false } = {}) {
  const opts = {
    method,
    headers: {
      'Content-Type': 'application/json',
      ...headers,
    },
  };

  if (body !== undefined) {
    opts.body = typeof body === 'string' ? body : JSON.stringify(body);
  }

  if (auth) {
    const token = localStorage.getItem('token');
    if (token) {
      opts.headers.Authorization = `Bearer ${token}`;
    }
  }

  // CRA proxy forwards /api/* to backend (see package.json proxy)
  const res = await fetch(`/api${path}`, opts);
  const text = await res.text();
  let data;
  try {
    data = text ? JSON.parse(text) : {};
  } catch {
    data = { raw: text };
  }

  if (!res.ok) {
    const message = data?.message || data?.error || res.statusText || 'Request failed';
    const error = new Error(message);
    error.status = res.status;
    error.data = data;
    throw error;
  }

  return data;
}

export function setAuth(token, user) {
  if (token) localStorage.setItem('token', token);
  if (user) localStorage.setItem('user', JSON.stringify(user));
}

export function clearAuth() {
  localStorage.removeItem('token');
  localStorage.removeItem('user');
}
