import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    company: '',
    password: '',
    confirmPassword: '',
    agreeTerms: false
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // ... (keep all the existing styles - just remove newsletter-related parts)

  // In the checkboxContainer section, remove the newsletter checkbox:
  
  return (
    <div style={styles.pageContainer}>
      <div style={styles.registerCard}>
        {/* Welcome Section (No Logo) */}
        <div style={styles.logoSection}>
          <h1 style={styles.companyName}>Join Trinix</h1>
          <p style={styles.welcomeText}>Create your professional account</p>
        </div>

        {/* ... (rest of form stays the same) */}

        <div style={styles.checkboxContainer}>
          <div style={styles.checkboxGroup}>
            <input
              type="checkbox"
              id="agreeTerms"
              name="agreeTerms"
              checked={formData.agreeTerms}
              onChange={handleInputChange}
              style={styles.checkbox}
            />
            <label htmlFor="agreeTerms" style={styles.checkboxLabel}>
              I agree to the{' '}
              <Link to="/terms" style={styles.termsLink} className="terms-link">
                Terms of Service
              </Link>{' '}
              and{' '}
              <Link to="/privacy" style={styles.termsLink} className="terms-link">
                Privacy Policy
              </Link>
            </label>
          </div>
          {errors.agreeTerms && (
            <div style={styles.errorMessage}>
              <span>⚠️</span>
              <span>{errors.agreeTerms}</span>
            </div>
          )}
          
          {/* NEWSLETTER CHECKBOX REMOVED */}
        </div>

        {/* ... (rest remains same) */}
      </div>
    </div>
  );
};
