import React, { useState } from 'react';
import { Mail, Phone, MapPin, Clock, Send, CheckCircle, MessageCircle, FileText, Wrench, Users } from 'lucide-react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    company: '',
    phone: '',
    subject: '',
    message: ''
  });
  
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitted(true);
    setTimeout(() => {
      setFormData({ name: '', email: '', company: '', phone: '', subject: '', message: '' });
      setIsSubmitted(false);
    }, 3000);
  };

  const helpCategories = [
    {
      icon: <FileText size={24} />,
      title: 'General Inquiry',
      description: 'Questions about our services and portfolio'
    },
    {
      icon: <Wrench size={24} />,
      title: 'Technical Support',
      description: 'Help with platform features and issues'
    },
    {
      icon: <MessageCircle size={24} />,
      title: 'Billing & Payments',
      description: 'Questions about pricing and payments'
    },
    {
      icon: <Users size={24} />,
      title: 'Partnership',
      description: 'Interested in collaboration opportunities'
    }
  ];

  const contactMethods = [
    {
      icon: <Mail size={28} />,
      title: 'Email Us',
      email: 'hello@trinix.com',
      description: 'Get in touch via email for general inquiries and support.',
      gradient: 'from-blue-500 to-cyan-500'
    },
    {
      icon: <Phone size={28} />,
      title: 'Call Us',
      phone: '+1 (555) 123-4567',
      support: '+91 (0000) 0-0000',
      description: 'Speak with our team for immediate assistance.',
      gradient: 'from-purple-500 to-pink-500'
    },
    {
      icon: <MapPin size={28} />,
      title: 'Visit Us',
      location: 'Mumbai, Maharashtra',
      address: 'India',
      description: 'Our headquarters for face-to-face meetings.',
      gradient: 'from-indigo-500 to-purple-500'
    },
    {
      icon: <Clock size={28} />,
      title: 'Business Hours',
      hours: 'Mon - Fri: 9:00 AM - 6:00 PM',
      weekend: 'Sat - Sun: 10:00 AM - 4:00 PM',
      description: 'We are available during our business hours for your convenience.',
      gradient: 'from-rose-500 to-orange-500'
    }
  ];

  const quickActions = [
    { icon: <MessageCircle size={20} />, text: 'Start Live Chat' },
    { icon: <Mail size={20} />, text: 'Send Email' },
    { icon: <Phone size={20} />, text: 'Call Now' }
  ];

  const responseTime = [
    { time: '24 Hours', label: 'Email Response Time', color: 'from-cyan-400 to-blue-500' },
    { time: '2 Hours', label: 'Live Chat Response', color: 'from-purple-400 to-pink-500' },
    { time: 'Immediate', label: 'Phone Support', color: 'from-rose-400 to-orange-500' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-purple-50">
      {/* Animated Background Elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-72 h-72 bg-purple-200/30 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-20 right-10 w-96 h-96 bg-blue-200/30 rounded-full blur-3xl animate-pulse" style={{animationDelay: '1s'}}></div>
      </div>

      {/* Hero Section */}
      <section className="relative pt-32 pb-12 px-6">
        <div className="max-w-7xl mx-auto text-center">
          <h1 className="text-5xl md:text-6xl font-bold text-slate-900 mb-4">
            Get in <span className="bg-gradient-to-r from-purple-600 via-blue-600 to-cyan-600 bg-clip-text text-transparent">Touch</span>
          </h1>
          <p className="text-lg text-slate-600 max-w-2xl mx-auto">
            Have questions or need support? We're here to help. Reach out to us through any of the channels below.
          </p>
        </div>
      </section>

      {/* Main Content Grid */}
      <section className="relative px-6 pb-20">
        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            
            {/* Left Column - Form */}
            <div className="space-y-8">
              {/* Contact Form */}
              <div className="bg-white/80 backdrop-blur-xl rounded-3xl border border-slate-200 p-8 shadow-xl">
                {isSubmitted ? (
                  <div className="text-center py-12">
                    <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-green-100 to-emerald-100 rounded-full mb-6">
                      <CheckCircle size={40} className="text-green-600" />
                    </div>
                    <h3 className="text-2xl font-bold text-slate-900 mb-3">Message Sent!</h3>
                    <p className="text-slate-600">We'll get back to you within 24 hours.</p>
                  </div>
                ) : (
                  <>
                    <h2 className="text-2xl font-bold text-slate-900 mb-6">Send us a Message</h2>
                    <div className="space-y-5">
                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <label className="block text-sm font-medium text-slate-700 mb-2">Your full name *</label>
                          <input 
                            type="text"
                            value={formData.name}
                            onChange={(e) => setFormData({...formData, name: e.target.value})}
                            placeholder="Your company name"
                            className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl text-slate-900 placeholder-slate-400 focus:outline-none focus:border-purple-400 focus:ring-2 focus:ring-purple-100 transition-all"
                          />
                        </div>
                        <div>
                          <label className="block text-sm font-medium text-slate-700 mb-2">Email *</label>
                          <input 
                            type="email"
                            value={formData.email}
                            onChange={(e) => setFormData({...formData, email: e.target.value})}
                            placeholder="john@example.com"
                            className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl text-slate-900 placeholder-slate-400 focus:outline-none focus:border-purple-400 focus:ring-2 focus:ring-purple-100 transition-all"
                          />
                        </div>
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-slate-700 mb-2">Company (Optional)</label>
                        <input 
                          type="text"
                          value={formData.company}
                          onChange={(e) => setFormData({...formData, company: e.target.value})}
                          placeholder="Your company name"
                          className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl text-slate-900 placeholder-slate-400 focus:outline-none focus:border-purple-400 focus:ring-2 focus:ring-purple-100 transition-all"
                        />
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-slate-700 mb-2">Subject *</label>
                        <input 
                          type="text"
                          value={formData.subject}
                          onChange={(e) => setFormData({...formData, subject: e.target.value})}
                          placeholder="Describe your subject"
                          className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl text-slate-900 placeholder-slate-400 focus:outline-none focus:border-purple-400 focus:ring-2 focus:ring-purple-100 transition-all"
                        />
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-slate-700 mb-2">Message *</label>
                        <textarea 
                          value={formData.message}
                          onChange={(e) => setFormData({...formData, message: e.target.value})}
                          rows="5"
                          placeholder="Tell us more about your inquiry..."
                          className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl text-slate-900 placeholder-slate-400 focus:outline-none focus:border-purple-400 focus:ring-2 focus:ring-purple-100 transition-all resize-none"
                        />
                      </div>

                      <button 
                        onClick={handleSubmit}
                        className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-500 hover:to-blue-500 text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 transform hover:scale-[1.02] hover:shadow-xl flex items-center justify-center gap-2"
                      >
                        <Send size={18} />
                        <span>Send Message</span>
                      </button>
                    </div>
                  </>
                )}
              </div>

              {/* What can we help you with */}
              <div className="bg-white/80 backdrop-blur-xl rounded-3xl border border-slate-200 p-8 shadow-xl">
                <h3 className="text-xl font-bold text-slate-900 mb-6">What can we help you with?</h3>
                <div className="space-y-4">
                  {helpCategories.map((category, index) => (
                    <div key={index} className="flex items-start gap-4 p-4 bg-slate-50 rounded-xl hover:bg-slate-100 transition-colors cursor-pointer">
                      <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-purple-100 to-blue-100 flex items-center justify-center text-purple-600 flex-shrink-0">
                        {category.icon}
                      </div>
                      <div>
                        <h4 className="font-semibold text-slate-900">{category.title}</h4>
                        <p className="text-sm text-slate-600">{category.description}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Right Column */}
            <div className="space-y-8">
              {/* Quick Actions */}
              <div className="bg-white/80 backdrop-blur-xl rounded-3xl border border-slate-200 p-8 shadow-xl">
                <h3 className="text-xl font-bold text-slate-900 mb-6">Quick Actions</h3>
                <div className="space-y-3">
                  {quickActions.map((action, index) => (
                    <button 
                      key={index}
                      className="w-full flex items-center justify-center gap-3 py-4 px-6 bg-gradient-to-r from-purple-50 to-blue-50 hover:from-purple-100 hover:to-blue-100 text-slate-900 font-medium rounded-xl transition-all border border-slate-200"
                    >
                      {action.icon}
                      <span>{action.text}</span>
                    </button>
                  ))}
                </div>
              </div>

              {/* Contact Methods */}
              {contactMethods.map((method, index) => (
                <div 
                  key={index}
                  className="bg-white/80 backdrop-blur-xl rounded-3xl border border-slate-200 p-8 shadow-xl hover:shadow-2xl transition-all duration-300"
                >
                  <div className="flex items-start gap-4 mb-4">
                    <div className={`w-14 h-14 rounded-xl bg-gradient-to-br ${method.gradient} flex items-center justify-center text-white shadow-lg flex-shrink-0`}>
                      {method.icon}
                    </div>
                    <div>
                      <h3 className="text-xl font-bold text-slate-900 mb-1">{method.title}</h3>
                      {method.email && <p className="text-purple-600 font-medium">{method.email}</p>}
                      {method.phone && <p className="text-purple-600 font-medium">{method.phone}</p>}
                      {method.support && <p className="text-slate-600 text-sm">{method.support}</p>}
                      {method.location && <p className="text-slate-900 font-medium">{method.location}</p>}
                      {method.address && <p className="text-slate-600">{method.address}</p>}
                      {method.hours && <p className="text-slate-900 font-medium">{method.hours}</p>}
                      {method.weekend && <p className="text-slate-600">{method.weekend}</p>}
                    </div>
                  </div>
                  <p className="text-sm text-slate-600 mt-3">{method.description}</p>
                </div>
              ))}

              {/* Visit Our Office */}
              <div className="bg-white/80 backdrop-blur-xl rounded-3xl border border-slate-200 p-8 shadow-xl">
                <h3 className="text-xl font-bold text-slate-900 mb-4">Visit Our Office</h3>
                <div className="space-y-2 mb-4">
                  <p className="text-slate-900 font-medium">Trinix Tech, LLP,</p>
                  <p className="text-slate-600">123 Tech Plaza</p>
                  <p className="text-slate-600">Colaba, Mumbai 400001</p>
                  <p className="text-slate-600">Maharashtra, India</p>
                </div>
                <div className="space-y-2 text-sm">
                  <p className="text-slate-700"><span className="font-semibold">Monday - Friday:</span> 9:00 AM - 6:00 PM</p>
                  <p className="text-slate-700"><span className="font-semibold">Saturday:</span> 10:00 AM - 4:00 PM</p>
                </div>
              </div>
            </div>
          </div>

          {/* Response Time Banner */}
          <div className="mt-12 bg-white/80 backdrop-blur-xl rounded-3xl border border-slate-200 p-8 shadow-xl">
            <h3 className="text-2xl font-bold text-slate-900 text-center mb-8">We Promise to Respond Quickly</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {responseTime.map((item, index) => (
                <div key={index} className="text-center">
                  <div className={`text-4xl font-bold bg-gradient-to-r ${item.color} bg-clip-text text-transparent mb-2`}>
                    {item.time}
                  </div>
                  <p className="text-slate-600 font-medium">{item.label}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      <style>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

export default Contact;
